// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title ERC20Mock
 * @dev Contrat ERC20 mock pour les tests
 * Permet de mint des tokens librement pour simuler diffrents scnarios
 */
contract ERC20Mock is ERC20 {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol
    ) ERC20(name, symbol) {
        _decimals = 18; // Dcimales par dfaut
    }

    /**
     * @dev Mint des tokens  une adresse spcifique
     * @param to Adresse qui recevra les tokens
     * @param amount Montant  minter
     */
    function mint(address to, uint256 amount) public {
        _mint(to, amount);
    }

    /**
     * @dev Burn des tokens d'une adresse spcifique
     * @param from Adresse dont les tokens seront brls
     * @param amount Montant  brler
     */
    function burn(address from, uint256 amount) public {
        _burn(from, amount);
    }

    /**
     * @dev Override pour retourner les dcimales personnalises
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Fonction utilitaire pour changer le nombre de dcimales (pour tests)
     */
    function setDecimals(uint8 newDecimals) public {
        _decimals = newDecimals;
    }

    /**
     * @dev Fonction utilitaire pour mint et approuver en une seule transaction
     */
    function mintAndApprove(address to, uint256 amount, address spender) public {
        _mint(to, amount);
        _approve(to, spender, amount);
    }

    /**
     * @dev Fonction pour simuler un chec de transfert (pour tests d'erreur)
     */
    bool private _transferShouldFail;

    function setTransferShouldFail(bool shouldFail) public {
        _transferShouldFail = shouldFail;
    }

    /**
     * @dev Override pour simuler des checs de transfert
     */
    function transfer(address to, uint256 amount) public virtual override returns (bool) {
        if (_transferShouldFail) {
            _transferShouldFail = false; // Reset after one failure
            return false;
        }
        return super.transfer(to, amount);
    }

    /**
     * @dev Override pour simuler des checs de transferFrom
     */
    function transferFrom(address from, address to, uint256 amount) public virtual override returns (bool) {
        if (_transferShouldFail) {
            _transferShouldFail = false; // Reset after one failure
            return false;
        }
        return super.transferFrom(from, to, amount);
    }

    /**
     * @dev Fonction utilitaire pour reset tous les balances (pour tests)
     */
    function resetBalance(address account) public {
        uint256 currentBalance = balanceOf(account);
        if (currentBalance > 0) {
            _burn(account, currentBalance);
        }
    }

    /**
     * @dev Fonction pour set directement un balance (pour tests rapides)
     */
    function setBalance(address account, uint256 amount) public {
        uint256 currentBalance = balanceOf(account);
        if (currentBalance < amount) {
            _mint(account, amount - currentBalance);
        } else if (currentBalance > amount) {
            _burn(account, currentBalance - amount);
        }
    }
}

contracts/mocks/MockVolatilityOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../interfaces/IVolatilityOracle.sol";

/**
 * @title MockVolatilityOracle
 * @dev Mock implementation of IVolatilityOracle for testing purposes
 */
contract MockVolatilityOracle is IVolatilityOracle {
    VolatilityData private volatilityData;
    address public owner;

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor() {
        owner = msg.sender;
        volatilityData = VolatilityData({
            currentPrice: 12000000, // $0.12 with 8 decimals
            volatilityPercent: 2000, // 20%
            lastUpdateTime: block.timestamp,
            emergencyMode: false
        });
    }

    function getCurrentCHZPrice() external view override returns (uint256) {
        return volatilityData.currentPrice;
    }

    function getVolatilityPercent() external view override returns (uint256) {
        return volatilityData.volatilityPercent;
    }

    function getVolatilityData() external view override returns (VolatilityData memory) {
        return volatilityData;
    }

    function shouldTriggerProtection(uint256 threshold) external view override returns (bool) {
        return volatilityData.volatilityPercent >= threshold;
    }

    function shouldTriggerEmergency(uint256 crashThreshold) external view override returns (bool) {
        return volatilityData.emergencyMode && volatilityData.volatilityPercent >= crashThreshold;
    }

    function updatePriceData() external override {
        volatilityData.lastUpdateTime = block.timestamp;
        emit PriceUpdated(volatilityData.currentPrice, volatilityData.volatilityPercent);
    }

    function setEmergencyMode(bool _emergencyMode) external override onlyOwner {
        volatilityData.emergencyMode = _emergencyMode;
        if (_emergencyMode) {
            emit EmergencyModeTriggered(volatilityData.volatilityPercent);
        }
    }

    // Test helper functions
    function setPrice(uint256 _price) external onlyOwner {
        volatilityData.currentPrice = _price;
        volatilityData.lastUpdateTime = block.timestamp;
        emit PriceUpdated(_price, volatilityData.volatilityPercent);
    }

    function setVolatilityPercent(uint256 _volatilityPercent) external onlyOwner {
        volatilityData.volatilityPercent = _volatilityPercent;
        volatilityData.lastUpdateTime = block.timestamp;
        emit VolatilityThresholdReached(_volatilityPercent, 3000);
    }

    function setVolatilityData(
        uint256 _price,
        uint256 _volatilityPercent,
        bool _emergencyMode
    ) external onlyOwner {
        volatilityData.currentPrice = _price;
        volatilityData.volatilityPercent = _volatilityPercent;
        volatilityData.emergencyMode = _emergencyMode;
        volatilityData.lastUpdateTime = block.timestamp;

        emit PriceUpdated(_price, _volatilityPercent);
        if (_emergencyMode) {
            emit EmergencyModeTriggered(_volatilityPercent);
        }
    }
}

contracts/SeasonalNFTArena.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./interfaces/IVolatilityOracle.sol";

/**
 * @title SeasonalNFTArena
 * @dev Long-term prediction arena with CHZ/USDC dual-asset pot and volatility protection
 */
contract SeasonalNFTArena is Ownable, ReentrancyGuard, Pausable {
    using SafeMath for uint256;

    // Constants
    uint256 public constant MAX_DURATION = 90 days; // 3 months maximum
    uint256 public constant VOLATILITY_THRESHOLD = 3000; // 30% volatility threshold
    uint256 public constant EMERGENCY_THRESHOLD = 4000; // 40% crash threshold
    uint256 public constant PLATFORM_FEE = 2000; // 20%
    uint256 public constant PARTNER_FEE = 1000; // 10%
    uint256 public constant IMMEDIATE_POT = 4000; // 40%
    uint256 public constant REINVESTMENT_FEE = 3000; // 30%
    uint256 public constant MIN_FAN_TOKENS = 2000 * 10**18; // 2000 fan tokens minimum
    uint256 public constant INSURANCE_FEE = 20 * 10**18; // 20 CHZ insurance

    // Tokens
    IERC20 public immutable chzToken;
    IERC20 public immutable usdcToken;
    IERC20 public immutable fanToken;
    IVolatilityOracle public volatilityOracle;

    // Arena counter
    uint256 public nextArenaId = 1;

    // Seasonal Types
    enum SeasonalType {
        ChampionsLeague,    // 3 months
        TransferWindow,     // 3 months
        BallonDor,         // 6 months max
        WorldCupQualifiers // 3 months cycles
    }

    // Arena structure
    struct SeasonalArena {
        string title;
        string[] outcomeNames;
        SeasonalType seasonalType;
        uint256 startTime;
        uint256 endTime;
        uint256 deadline;
        bool isResolved;
        uint256 winningOutcomeId;

        // Pot management
        uint256 chzPot;
        uint256 usdcPot;
        uint256 totalAccumulated;

        // Entry requirements
        uint256 entryFeeCHZ;
        bool isActive;

        // Protection
        bool protectionTriggered;
        uint256 lastVolatilityCheck;
    }

    // Betting structure
    struct BetPosition {
        uint256 chzAmount;
        uint256 usdcAmount;
        bool hasInsurance;
        uint256 timestamp;
    }

    // Storage
    mapping(uint256 => SeasonalArena) public seasonalArenas;
    mapping(uint256 => mapping(uint256 => uint256)) public outcomeCHZPots;
    mapping(uint256 => mapping(uint256 => uint256)) public outcomeUSDCPots;
    mapping(uint256 => mapping(uint256 => mapping(address => BetPosition))) public betPositions;
    mapping(uint256 => uint256) public arenaAccumulation; // Reinvestment tracking

    // Events
    event SeasonalArenaCreated(
        uint256 indexed arenaId,
        string title,
        SeasonalType seasonalType,
        uint256 entryFee,
        uint256 deadline
    );

    event SeasonalBetPlaced(
        uint256 indexed arenaId,
        address indexed user,
        uint256 outcomeId,
        uint256 chzAmount,
        bool hasInsurance
    );

    event VolatilityProtectionTriggered(
        uint256 indexed arenaId,
        uint256 chzConverted,
        uint256 usdcReceived,
        uint256 volatilityPercent
    );

    event EmergencyConversionTriggered(
        uint256 indexed arenaId,
        uint256 chzConverted,
        uint256 usdcReceived
    );

    event PotAccumulated(
        uint256 indexed arenaId,
        uint256 chzAmount,
        uint256 newPotTotal
    );

    event SeasonalResolved(
        uint256 indexed arenaId,
        uint256 winningOutcomeId,
        uint256 totalPotCHZ,
        uint256 totalPotUSDC
    );

    event MegaPrizeWon(
        uint256 indexed arenaId,
        address indexed winner,
        uint256 chzAmount,
        uint256 usdcAmount
    );

    constructor(
        address _chzToken,
        address _usdcToken,
        address _fanToken,
        address _volatilityOracle
    ) Ownable() {
        chzToken = IERC20(_chzToken);
        usdcToken = IERC20(_usdcToken);
        fanToken = IERC20(_fanToken);
        volatilityOracle = IVolatilityOracle(_volatilityOracle);
    }

    /**
     * @dev Create a new seasonal arena
     */
    function createSeasonalArena(
        string memory _title,
        string[] memory _outcomeNames,
        SeasonalType _seasonalType,
        uint256 _entryFeeCHZ,
        uint256 _duration
    ) external onlyOwner {
        require(_duration <= MAX_DURATION, "Duration exceeds maximum");
        require(_entryFeeCHZ >= 150 * 10**18 && _entryFeeCHZ <= 500 * 10**18, "Invalid entry fee range");
        require(_outcomeNames.length >= 2, "Need at least 2 outcomes");

        uint256 arenaId = nextArenaId++;
        uint256 deadline = block.timestamp + _duration;

        seasonalArenas[arenaId] = SeasonalArena({
            title: _title,
            outcomeNames: _outcomeNames,
            seasonalType: _seasonalType,
            startTime: block.timestamp,
            endTime: deadline,
            deadline: deadline,
            isResolved: false,
            winningOutcomeId: 0,
            chzPot: 0,
            usdcPot: 0,
            totalAccumulated: 0,
            entryFeeCHZ: _entryFeeCHZ,
            isActive: true,
            protectionTriggered: false,
            lastVolatilityCheck: block.timestamp
        });

        emit SeasonalArenaCreated(arenaId, _title, _seasonalType, _entryFeeCHZ, deadline);
    }

    /**
     * @dev Enter a seasonal arena with optional insurance
     */
    function enterSeasonal(
        uint256 _arenaId,
        uint256 _outcomeId,
        bool _withInsurance
    ) external nonReentrant whenNotPaused {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        require(arena.isActive, "Arena not active");
        require(!arena.isResolved, "Arena already resolved");
        require(block.timestamp < arena.deadline, "Betting deadline passed");
        require(_outcomeId < arena.outcomeNames.length, "Invalid outcome");
        require(fanToken.balanceOf(msg.sender) >= MIN_FAN_TOKENS, "Insufficient fan tokens");

        uint256 totalFee = _withInsurance ? arena.entryFeeCHZ.add(INSURANCE_FEE) : arena.entryFeeCHZ;
        require(chzToken.transferFrom(msg.sender, address(this), totalFee), "CHZ transfer failed");

        _processBetEntry(_arenaId, _outcomeId, _withInsurance, arena.entryFeeCHZ);
    }

    /**
     * @dev Internal function to process bet entry and fee distribution
     */
    function _processBetEntry(
        uint256 _arenaId,
        uint256 _outcomeId,
        bool _withInsurance,
        uint256 _entryFee
    ) internal {
        // Calculate fee distributions
        uint256 immediatePot = _entryFee.mul(IMMEDIATE_POT).div(10000);
        uint256 reinvestment = _entryFee.mul(REINVESTMENT_FEE).div(10000);

        // Split immediate pot: 60% CHZ, 40% USDC
        uint256 chzForPot = immediatePot.mul(6000).div(10000);
        uint256 chzForUSDC = immediatePot.sub(chzForPot);

        SeasonalArena storage arena = seasonalArenas[_arenaId];
        arena.chzPot = arena.chzPot.add(chzForPot);
        outcomeCHZPots[_arenaId][_outcomeId] = outcomeCHZPots[_arenaId][_outcomeId].add(chzForPot);

        uint256 usdcAmount = 0;
        if (chzForUSDC > 0) {
            usdcAmount = _convertCHZToUSDC(chzForUSDC);
            arena.usdcPot = arena.usdcPot.add(usdcAmount);
            outcomeUSDCPots[_arenaId][_outcomeId] = outcomeUSDCPots[_arenaId][_outcomeId].add(usdcAmount);
        }

        // Store bet position
        betPositions[_arenaId][_outcomeId][msg.sender] = BetPosition({
            chzAmount: chzForPot,
            usdcAmount: usdcAmount,
            hasInsurance: _withInsurance,
            timestamp: block.timestamp
        });

        arenaAccumulation[_arenaId] = arenaAccumulation[_arenaId].add(reinvestment);
        emit SeasonalBetPlaced(_arenaId, msg.sender, _outcomeId, _entryFee, _withInsurance);
    }

    /**
     * @dev Trigger volatility protection automatically
     */
    function triggerVolatilityProtection(uint256 _arenaId) external {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        require(arena.isActive && !arena.isResolved, "Arena not active");
        require(!arena.protectionTriggered, "Protection already triggered");
        require(
            block.timestamp >= arena.lastVolatilityCheck + 1 hours,
            "Too soon to check volatility"
        );

        require(
            volatilityOracle.shouldTriggerProtection(VOLATILITY_THRESHOLD),
            "Volatility threshold not reached"
        );

        arena.lastVolatilityCheck = block.timestamp;
        arena.protectionTriggered = true;

        // Convert 50% of CHZ pot to USDC
        uint256 chzToConvert = arena.chzPot.mul(5000).div(10000);
        uint256 usdcReceived = _convertCHZToUSDC(chzToConvert);

        arena.chzPot = arena.chzPot.sub(chzToConvert);
        arena.usdcPot = arena.usdcPot.add(usdcReceived);

        emit VolatilityProtectionTriggered(
            _arenaId,
            chzToConvert,
            usdcReceived,
            volatilityOracle.getVolatilityPercent()
        );
    }

    /**
     * @dev Emergency conversion for severe CHZ crash
     */
    function emergencyConvert(uint256 _arenaId) external onlyOwner {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        require(arena.isActive && !arena.isResolved, "Arena not active");
        require(
            volatilityOracle.shouldTriggerEmergency(EMERGENCY_THRESHOLD),
            "Emergency threshold not reached"
        );

        // Convert 70% of CHZ pot to USDC
        uint256 chzToConvert = arena.chzPot.mul(7000).div(10000);
        uint256 usdcReceived = _convertCHZToUSDC(chzToConvert);

        arena.chzPot = arena.chzPot.sub(chzToConvert);
        arena.usdcPot = arena.usdcPot.add(usdcReceived);

        emit EmergencyConversionTriggered(_arenaId, chzToConvert, usdcReceived);
    }

    /**
     * @dev Accumulate platform fees into arena pot
     */
    function accumulateIntoPot(uint256 _arenaId) external onlyOwner {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        require(arena.isActive, "Arena not active");

        uint256 accumulatedAmount = arenaAccumulation[_arenaId];
        require(accumulatedAmount > 0, "No accumulated funds");

        // Split accumulation: 60% CHZ, 40% converted to USDC
        uint256 chzForPot = accumulatedAmount.mul(6000).div(10000);
        uint256 chzForUSDC = accumulatedAmount.sub(chzForPot);

        arena.chzPot = arena.chzPot.add(chzForPot);
        arena.totalAccumulated = arena.totalAccumulated.add(accumulatedAmount);

        if (chzForUSDC > 0) {
            uint256 usdcAmount = _convertCHZToUSDC(chzForUSDC);
            arena.usdcPot = arena.usdcPot.add(usdcAmount);
        }

        arenaAccumulation[_arenaId] = 0;

        emit PotAccumulated(_arenaId, accumulatedAmount, arena.chzPot + arena.usdcPot);
    }

    /**
     * @dev Resolve seasonal arena
     */
    function resolveSeasonal(uint256 _arenaId, uint256 _winningOutcomeId) external onlyOwner {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        require(arena.isActive, "Arena not active");
        require(!arena.isResolved, "Already resolved");
        require(_winningOutcomeId < arena.outcomeNames.length, "Invalid outcome");

        arena.isResolved = true;
        arena.winningOutcomeId = _winningOutcomeId;
        arena.isActive = false;

        emit SeasonalResolved(_arenaId, _winningOutcomeId, arena.chzPot, arena.usdcPot);
    }

    /**
     * @dev Claim seasonal rewards with mega-prize distribution
     */
    function claimSeasonalReward(uint256 _arenaId) external nonReentrant {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        require(arena.isResolved, "Arena not resolved");

        uint256 winningOutcome = arena.winningOutcomeId;
        BetPosition storage position = betPositions[_arenaId][winningOutcome][msg.sender];
        require(position.chzAmount > 0 || position.usdcAmount > 0, "No winning position");

        (uint256 chzReward, uint256 usdcReward) = _calculateRewards(_arenaId, winningOutcome, position);

        // Clear position
        delete betPositions[_arenaId][winningOutcome][msg.sender];

        // Transfer rewards
        _transferRewards(chzReward, usdcReward);

        emit MegaPrizeWon(_arenaId, msg.sender, chzReward, usdcReward);
    }

    /**
     * @dev Internal function to calculate rewards
     */
    function _calculateRewards(
        uint256 _arenaId,
        uint256 _winningOutcome,
        BetPosition storage _position
    ) internal view returns (uint256 chzReward, uint256 usdcReward) {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        uint256 totalWinningCHZ = outcomeCHZPots[_arenaId][_winningOutcome];
        uint256 totalWinningUSDC = outcomeUSDCPots[_arenaId][_winningOutcome];

        require(totalWinningCHZ > 0 || totalWinningUSDC > 0, "No winning pot");

        // CHZ rewards (80% of pot)
        if (totalWinningCHZ > 0 && _position.chzAmount > 0) {
            chzReward = arena.chzPot.mul(8000).div(10000).mul(_position.chzAmount).div(totalWinningCHZ);
        }

        // USDC rewards (80% of pot)
        if (totalWinningUSDC > 0 && _position.usdcAmount > 0) {
            usdcReward = arena.usdcPot.mul(8000).div(10000).mul(_position.usdcAmount).div(totalWinningUSDC);
        }

        // Insurance bonus
        if (_position.hasInsurance && arena.protectionTriggered) {
            chzReward = chzReward.add(INSURANCE_FEE.mul(2));
        }
    }

    /**
     * @dev Internal function to transfer rewards
     */
    function _transferRewards(uint256 _chzReward, uint256 _usdcReward) internal {
        if (_chzReward > 0) {
            require(chzToken.transfer(msg.sender, _chzReward), "CHZ transfer failed");
        }
        if (_usdcReward > 0) {
            require(usdcToken.transfer(msg.sender, _usdcReward), "USDC transfer failed");
        }
    }

    /**
     * @dev Internal function to simulate CHZ to USDC conversion
     * In production, this would integrate with a DEX like Uniswap
     */
    function _convertCHZToUSDC(uint256 _chzAmount) internal view returns (uint256) {
        uint256 chzPriceInUSD = volatilityOracle.getCurrentCHZPrice();
        // Assuming USDC has 6 decimals and CHZ has 18 decimals
        // Price is in 8 decimals from oracle
        return _chzAmount.mul(chzPriceInUSD).div(10**20); // 18 + 8 - 6 = 20
    }

    /**
     * @dev Update volatility oracle
     */
    function updateVolatilityOracle(address _newOracle) external onlyOwner {
        volatilityOracle = IVolatilityOracle(_newOracle);
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get arena details
     */
    function getArenaDetails(uint256 _arenaId) external view returns (
        string memory title,
        SeasonalType seasonalType,
        uint256 chzPot,
        uint256 usdcPot,
        uint256 deadline,
        bool isResolved,
        bool isActive
    ) {
        SeasonalArena storage arena = seasonalArenas[_arenaId];
        return (
            arena.title,
            arena.seasonalType,
            arena.chzPot,
            arena.usdcPot,
            arena.deadline,
            arena.isResolved,
            arena.isActive
        );
    }

    /**
     * @dev Get user bet position
     */
    function getBetPosition(uint256 _arenaId, uint256 _outcomeId, address _user) external view returns (
        uint256 chzAmount,
        uint256 usdcAmount,
        bool hasInsurance,
        uint256 timestamp
    ) {
        BetPosition storage position = betPositions[_arenaId][_outcomeId][_user];
        return (position.chzAmount, position.usdcAmount, position.hasInsurance, position.timestamp);
    }

    /**
     * @dev Emergency withdrawal (owner only)
     */
    function emergencyWithdraw(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(owner(), _amount);
    }
}

contracts/SimpleDuel.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract SimpleDuel is Ownable, ReentrancyGuard, Pausable {
    IERC20 public immutable bettingToken;
    IERC20 public immutable fanToken;
    uint8 public immutable fanTokenDecimals;
    uint8 public immutable bettingTokenDecimals;

    uint256 public platformFeePercent = 300;
    uint256 public constant MAX_FEE_PERCENT = 1000; // Maximum 10%
    uint256 public nextDuelId;
    uint256 public pointsPerBetUnit = 20;
    uint256 public fanTokenThreshold;
    uint256 public totalFeesAccumulated;

    // User creation features
    uint256 public duelCreationFee = 10 * 10**18; // 10 CHZ par dfaut
    bool public userCreationEnabled = true;
    uint256 public constant MAX_USER_DEADLINE = 30 days; // Maximum 30 jours pour les utilisateurs

    // Gas relayer system
    mapping(address => uint256) public gasFeeDebt;
    address public gasRelayer;
    uint256 public gasAdvanceFee = 0.1 * 10**18; // ~0.1 USD equivalent in CHZ

    // Points system
    mapping(address => uint256) public userPoints;
    uint256 public constant POINTS_PER_DUEL = 50;
    uint256 public constant ARENA_ACTIVATION_POINTS = 100;

    // Arena eligibility management
    mapping(uint256 => bool) public duelArenaEligible;
    mapping(address => bool) public moderators;

    uint256 public constant RESOLUTION_TIMELOCK = 24 hours;
    mapping(bytes32 => uint256) public pendingResolutions;

    struct Duel {
        string title;
        string category;
        bool deprecated_arenaEligible; // Deprecated, use duelArenaEligible mapping instead
        string[3] outcomes;
        uint256 deadline;
        bool isResolved;
        bool isCanceled;
        uint256 winningOutcomeId;
        uint256 potTotal;
        uint256 feesCollected;
        uint256[3] outcomePots;
        address creator; // Crateur du duel
        mapping(address => uint256[3]) betsPerOutcome;
        mapping(address => bool) hasClaimedGains;
        mapping(address => bool) hasClaimedRefund;
    }

    mapping(uint256 => Duel) public duels;
    mapping(uint256 => mapping(address => uint256)) public fanTokenBalanceSnapshot;
    mapping(address => bool) public authorizedResolvers;

    event DuelCreated(uint256 indexed duelId, address indexed creator, string title, string category, string[3] outcomes, uint256 deadline);
    event DuelCreationFeeUpdated(uint256 newFee);
    event ArenaEligibilitySet(uint256 indexed duelId, bool eligible);
    event PointsAwarded(address indexed user, uint256 points, string reason);
    event PointsDeducted(address indexed user, uint256 points, string reason);
    event GasAdvanced(address indexed user, uint256 amount);
    event GasDebtRepaid(address indexed user, uint256 amount);
    event ModeratorSet(address indexed moderator, bool status);
    event BetPlaced(uint256 indexed duelId, address indexed user, uint256 outcomeId, uint256 amount, uint256 fanTokenBalance);
    event PointsEarned(address indexed user, uint256 duelId, uint256 points);
    event ArenaEligibleBet(address indexed user, uint256 duelId);
    event DuelResolved(uint256 indexed duelId, uint256 winningOutcomeId);
    event DuelCanceled(uint256 indexed duelId);
    event GainsClaimed(uint256 indexed duelId, address indexed user, uint256 amount, uint256 bonus);
    event RefundIssued(uint256 indexed duelId, address indexed user, uint256 amount);
    event FeesWithdrawn(address indexed to, uint256 amount);
    event ResolverAuthorized(address indexed resolver, bool authorized);
    event ResolutionProposed(uint256 indexed duelId, uint256 winningOutcomeId, uint256 executeAt);

    modifier onlyAuthorizedResolver() {
        require(authorizedResolvers[msg.sender] || msg.sender == owner(), "Not authorized to resolve");
        _;
    }

    modifier onlyModerator() {
        require(moderators[msg.sender] || msg.sender == owner(), "Not authorized moderator");
        _;
    }

    constructor(
        address _bettingTokenAddress,
        address _fanTokenAddress,
        uint8 _fanTokenDecimals,
        uint8 _bettingTokenDecimals
    ) Ownable() {
        require(_bettingTokenAddress != address(0), "Invalid betting token");
        require(_fanTokenAddress != address(0), "Invalid fan token");

        bettingToken = IERC20(_bettingTokenAddress);
        fanToken = IERC20(_fanTokenAddress);
        fanTokenDecimals = _fanTokenDecimals;
        bettingTokenDecimals = _bettingTokenDecimals;
        fanTokenThreshold = 100 * 10**_fanTokenDecimals;

        authorizedResolvers[msg.sender] = true;
        moderators[msg.sender] = true;
        gasRelayer = msg.sender;
    }

    // READ Functions
    function getBettingToken() external view returns (address) {
        return address(bettingToken);
    }

    function getFanToken() external view returns (address) {
        return address(fanToken);
    }

    function getPlatformFeePercent() external view returns (uint256) {
        return platformFeePercent;
    }

    function getDuelInfo(uint256 _duelId) external view returns (
        string memory title,
        string memory category,
        bool arenaEligible,
        string[3] memory outcomes,
        uint256 potTotal,
        uint256 deadline,
        bool isResolved,
        bool isCanceled,
        uint256 winningOutcomeId,
        address creator
    ) {
        Duel storage duel = duels[_duelId];
        return (
            duel.title,
            duel.category,
            duelArenaEligible[_duelId],
            duel.outcomes,
            duel.potTotal,
            duel.deadline,
            duel.isResolved,
            duel.isCanceled,
            duel.winningOutcomeId,
            duel.creator
        );
    }

    function getOutcomePot(uint256 _duelId, uint256 _outcomeId) external view returns (uint256) {
        require(_outcomeId < 3, "Invalid outcome ID");
        return duels[_duelId].outcomePots[_outcomeId];
    }

    function getUserBets(uint256 _duelId, address _user) external view returns (uint256[3] memory) {
        return [
            duels[_duelId].betsPerOutcome[_user][0],
            duels[_duelId].betsPerOutcome[_user][1],
            duels[_duelId].betsPerOutcome[_user][2]
        ];
    }

    function calculatePotentialGains(uint256 _duelId, address _user) external view returns (uint256 baseGains, uint256 bonusGains) {
        Duel storage duel = duels[_duelId];
        if (!duel.isResolved || duel.isCanceled) return (0, 0);

        uint256 winningId = duel.winningOutcomeId;
        uint256 betAmount = duel.betsPerOutcome[_user][winningId];
        if (betAmount == 0) return (0, 0);

        uint256 potWinner = duel.outcomePots[winningId];
        uint256 potNet = duel.potTotal - duel.feesCollected;
        baseGains = (betAmount * potNet) / potWinner;

        if (baseGains < betAmount) {
            baseGains = betAmount;
        }

        // Vrifier le bonus avec le snapshot
        uint256 snapshotBalance = fanTokenBalanceSnapshot[_duelId][_user];
        if (snapshotBalance >= fanTokenThreshold) {
            bonusGains = (baseGains * 500) / 10000; // 5% bonus
        }
    }

    function isDuelBalanced(uint256 _duelId) public view returns (bool) {
        Duel storage duel = duels[_duelId];
        if (duel.potTotal == 0) return false;

        uint256 maxOutcomePot = 0;
        uint256 nonEmptyOutcomes = 0;

        for (uint256 i = 0; i < 3; i++) {
            if (duel.outcomePots[i] > 0) {
                nonEmptyOutcomes++;
                if (duel.outcomePots[i] > maxOutcomePot) {
                    maxOutcomePot = duel.outcomePots[i];
                }
            }
        }

        return nonEmptyOutcomes >= 2 && 
               maxOutcomePot < (duel.potTotal * 8000) / 10000; // Max 80%
    }

    // WRITE Functions
    function createDuel(
        string memory _title,
        string memory _category,
        string[3] memory _outcomes,
        uint256 _deadline
    ) external nonReentrant whenNotPaused {
        require(userCreationEnabled, "User creation is disabled");
        require(_deadline > block.timestamp + 1 hours, "Deadline too soon");
        require(_deadline < block.timestamp + MAX_USER_DEADLINE, "Deadline too far");
        require(bytes(_title).length > 0 && bytes(_title).length <= 100, "Invalid title");
        require(bytes(_category).length > 0 && bytes(_category).length <= 50, "Invalid category");

        for (uint256 i = 0; i < 3; i++) {
            require(bytes(_outcomes[i]).length > 0 && bytes(_outcomes[i]).length <= 50, "Invalid outcome");
        }

        uint256 finalCreationFee = duelCreationFee;

        // Exonration de frais pour dtenteurs de 100+ fan tokens
        if (fanToken.balanceOf(msg.sender) >= fanTokenThreshold) {
            finalCreationFee = 0;
        }

        // Avancer les frais de gas via relayer
        if (msg.sender != gasRelayer) {
            gasFeeDebt[msg.sender] += gasAdvanceFee;
            emit GasAdvanced(msg.sender, gasAdvanceFee);
        }

        // Payer les frais de cration si > 0
        if (finalCreationFee > 0) {
            require(bettingToken.balanceOf(msg.sender) >= finalCreationFee, "Insufficient balance for creation fee");
            require(bettingToken.transferFrom(msg.sender, address(this), finalCreationFee), "Creation fee payment failed");
            totalFeesAccumulated += finalCreationFee;
        }

        uint256 duelId = nextDuelId;
        Duel storage duel2 = duels[duelId];
        duel2.title = _title;
        duel2.category = _category;
        duel2.outcomes = _outcomes;
        duel2.deadline = _deadline;
        duel2.creator = msg.sender;

        // Attribuer 50 points pour la cration du duel
        userPoints[msg.sender] += POINTS_PER_DUEL;
        emit PointsAwarded(msg.sender, POINTS_PER_DUEL, "Duel creation");

        emit DuelCreated(duelId, msg.sender, _title, _category, _outcomes, _deadline);
        nextDuelId++;
    }

    function adminCreateDuel(
        string memory _title,
        string memory _category,
        bool _arenaEligible,
        string[3] memory _outcomes,
        uint256 _deadline
    ) external onlyOwner whenNotPaused {
        require(_deadline > block.timestamp + 1 hours, "Deadline too soon");
        require(_deadline < block.timestamp + 365 days, "Deadline too far");
        require(bytes(_title).length > 0 && bytes(_title).length <= 100, "Invalid title");
        require(bytes(_category).length > 0 && bytes(_category).length <= 50, "Invalid category");

        for (uint256 i = 0; i < 3; i++) {
            require(bytes(_outcomes[i]).length > 0 && bytes(_outcomes[i]).length <= 50, "Invalid outcome");
        }

        uint256 duelId = nextDuelId;
        Duel storage duel2 = duels[duelId];
        duel2.title = _title;
        duel2.category = _category;
        duel2.outcomes = _outcomes;
        duel2.deadline = _deadline;
        duel2.creator = msg.sender;

        // Admin peut directement dfinir l'ligibilit arne
        if (_arenaEligible) {
            duelArenaEligible[duelId] = true;
        }

        emit DuelCreated(duelId, msg.sender, _title, _category, _outcomes, _deadline);
        if (_arenaEligible) {
            emit ArenaEligibilitySet(duelId, true);
        }
        nextDuelId++;
    }

    function placeBet(uint256 _duelId, uint256 _outcomeId, uint256 _amount) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        Duel storage duel2 = duels[_duelId];
        require(_duelId < nextDuelId, "Duel does not exist");
        require(!duel2.isResolved && !duel2.isCanceled, "Duel is resolved or canceled");
        require(block.timestamp < duel2.deadline, "Betting deadline has passed");
        require(_outcomeId < 3, "Invalid outcome ID");
        require(_amount > 0, "Amount must be positive");

        // Vrifier les limites de pari
        uint256 minBet = 1 * 10**bettingTokenDecimals; // 1 token minimum
        uint256 maxBet = 10000 * 10**bettingTokenDecimals; 
        require(_amount >= minBet && _amount <= maxBet, "Amount out of bounds");

        require(bettingToken.transferFrom(msg.sender, address(this), _amount), "Token transfer failed");

        // Snapshot du solde fan token lors du premier pari
        if (fanTokenBalanceSnapshot[_duelId][msg.sender] == 0) {
            fanTokenBalanceSnapshot[_duelId][msg.sender] = fanToken.balanceOf(msg.sender);
        }

        duel2.potTotal += _amount;
        duel2.outcomePots[_outcomeId] += _amount;
        duel2.betsPerOutcome[msg.sender][_outcomeId] += _amount;

        // Calculer les points en fonction des dcimales du token
        uint256 points = (_amount * pointsPerBetUnit) / (10**bettingTokenDecimals);
        uint256 fanTokenBalance = fanToken.balanceOf(msg.sender);

        emit BetPlaced(_duelId, msg.sender, _outcomeId, _amount, fanTokenBalance);
        emit PointsEarned(msg.sender, _duelId, points);

        if (duelArenaEligible[_duelId]) {
            emit ArenaEligibleBet(msg.sender, _duelId);
        }
    }

    function proposeResolution(uint256 _duelId, uint256 _winningOutcomeId) 
        external 
        onlyAuthorizedResolver 
    {
        Duel storage duel2 = duels[_duelId];
        require(!duel2.isResolved && !duel2.isCanceled, "Duel is resolved or canceled");
        require(_winningOutcomeId < 3, "Invalid winning outcome");
        require(block.timestamp >= duel2.deadline, "Deadline not reached");
        require(duel2.outcomePots[_winningOutcomeId] > 0, "No bets on winning outcome");
        require(isDuelBalanced(_duelId), "Duel is not balanced");

        bytes32 proposalId = keccak256(abi.encode(_duelId, _winningOutcomeId));
        uint256 executeAt = block.timestamp + RESOLUTION_TIMELOCK;
        pendingResolutions[proposalId] = executeAt;

        emit ResolutionProposed(_duelId, _winningOutcomeId, executeAt);
    }

    function executeResolution(uint256 _duelId, uint256 _winningOutcomeId) 
        external 
        onlyAuthorizedResolver 
    {
        bytes32 proposalId = keccak256(abi.encode(_duelId, _winningOutcomeId));
        require(pendingResolutions[proposalId] != 0, "No pending resolution");
        require(block.timestamp >= pendingResolutions[proposalId], "Timelock not expired");

        Duel storage duel2 = duels[_duelId];
        require(!duel2.isResolved && !duel2.isCanceled, "Duel is resolved or canceled");

        // Calculer et stocker les fees
        uint256 fees = (duel2.potTotal * platformFeePercent) / 10000;
        duel2.feesCollected = fees;
        totalFeesAccumulated += fees;

        duel2.isResolved = true;
        duel2.winningOutcomeId = _winningOutcomeId;

        delete pendingResolutions[proposalId];

        emit DuelResolved(_duelId, _winningOutcomeId);
    }

    function validateAndCancelDuel(uint256 _duelId) external onlyOwner {
        Duel storage duel2 = duels[_duelId];
        require(!duel2.isResolved && !duel2.isCanceled, "Duel is resolved or already canceled");
        require(block.timestamp >= duel2.deadline, "Deadline not reached");
        require(!isDuelBalanced(_duelId), "Duel is balanced and cannot be canceled");

        duel2.isCanceled = true;
        emit DuelCanceled(_duelId);
    }

    function claimGains(uint256 _duelId) external nonReentrant {
        Duel storage duel2 = duels[_duelId];
        require(duel2.isResolved, "Duel is not resolved");
        require(!duel2.isCanceled, "Duel is canceled");
        require(!duel2.hasClaimedGains[msg.sender], "Already claimed");

        uint256 winningId = duel2.winningOutcomeId;
        uint256 betAmount = duel2.betsPerOutcome[msg.sender][winningId];
        require(betAmount > 0, "No winning bets");

        duel2.hasClaimedGains[msg.sender] = true;

        uint256 potWinner = duel2.outcomePots[winningId];
        uint256 potNet = duel2.potTotal - duel2.feesCollected;
        uint256 baseGain = (betAmount * potNet) / potWinner;

        if (baseGain < betAmount) {
            baseGain = betAmount;
        }

        uint256 bonus = 0;
        uint256 snapshotBalance = fanTokenBalanceSnapshot[_duelId][msg.sender];
        if (snapshotBalance >= fanTokenThreshold) {
            bonus = (baseGain * 500) / 10000; // 5% bonus
        }

        // Rembourser la dette de gas si applicable
        uint256 gasDebt = gasFeeDebt[msg.sender];
        uint256 gasRepayment = 0;
        if (gasDebt > 0) {
            gasRepayment = gasDebt > baseGain ? baseGain : gasDebt;
            gasFeeDebt[msg.sender] -= gasRepayment;
            baseGain -= gasRepayment;
            emit GasDebtRepaid(msg.sender, gasRepayment);
        }

        uint256 totalPayout = baseGain + bonus;
        require(bettingToken.balanceOf(address(this)) >= totalPayout, "Contract has insufficient balance");
        require(bettingToken.transfer(msg.sender, totalPayout), "Payout failed");

        emit GainsClaimed(_duelId, msg.sender, baseGain, bonus);
    }

    function claimRefund(uint256 _duelId) external nonReentrant {
        Duel storage duel2 = duels[_duelId];
        require(duel2.isCanceled, "Duel is not canceled");
        require(!duel2.hasClaimedRefund[msg.sender], "Already claimed refund");

        uint256 refundAmount = duel2.betsPerOutcome[msg.sender][0] + 
                              duel2.betsPerOutcome[msg.sender][1] + 
                              duel2.betsPerOutcome[msg.sender][2];
        require(refundAmount > 0, "No bets to refund");

        duel2.hasClaimedRefund[msg.sender] = true;
        require(bettingToken.balanceOf(address(this)) >= refundAmount, "Contract has insufficient balance");
        require(bettingToken.transfer(msg.sender, refundAmount), "Refund failed");

        emit RefundIssued(_duelId, msg.sender, refundAmount);
    }

    // Admin Functions
    function withdrawFees(address _to, uint256 _amount) external onlyOwner {
        require(_to != address(0), "Invalid address");
        require(_amount <= totalFeesAccumulated, "Amount exceeds accumulated fees");
        require(bettingToken.balanceOf(address(this)) >= _amount, "Contract has insufficient balance");

        totalFeesAccumulated -= _amount;
        require(bettingToken.transfer(_to, _amount), "Fee withdrawal failed");

        emit FeesWithdrawn(_to, _amount);
    }

    function setPlatformFeePercent(uint256 _newFee) external onlyOwner {
        require(_newFee <= MAX_FEE_PERCENT, "Fee too high");
        platformFeePercent = _newFee;
    }

    function setFanTokenThreshold(uint256 _newThreshold) external onlyOwner {
        fanTokenThreshold = _newThreshold;
    }

    function setPointsPerBetUnit(uint256 _newPoints) external onlyOwner {
        require(_newPoints > 0 && _newPoints <= 1000, "Invalid points value");
        pointsPerBetUnit = _newPoints;
    }

    function authorizeResolver(address _resolver, bool _authorized) external onlyOwner {
        require(_resolver != address(0), "Invalid resolver address");
        authorizedResolvers[_resolver] = _authorized;
        emit ResolverAuthorized(_resolver, _authorized);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function emergencyWithdraw(address _token, address _to, uint256 _amount) external onlyOwner {
        require(_to != address(0), "Invalid address");
        IERC20(_token).transfer(_to, _amount);
    }

    function setDuelCreationFee(uint256 _newFee) external onlyOwner {
        duelCreationFee = _newFee;
        emit DuelCreationFeeUpdated(_newFee);
    }

    function toggleUserCreation(bool _enabled) external onlyOwner {
        userCreationEnabled = _enabled;
    }

    function transferOwnership(address newOwner) public override onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        _transferOwnership(newOwner);
    }

    // New functions for simplified duel management
    function setArenaEligible(uint256 _duelId, bool _eligible) external onlyModerator {
        require(_duelId < nextDuelId, "Duel does not exist");
        duelArenaEligible[_duelId] = _eligible;
        emit ArenaEligibilitySet(_duelId, _eligible);
    }

    function setModerator(address _moderator, bool _status) external onlyOwner {
        require(_moderator != address(0), "Invalid moderator address");
        moderators[_moderator] = _status;
        emit ModeratorSet(_moderator, _status);
    }

    function setGasRelayer(address _relayer) external onlyOwner {
        require(_relayer != address(0), "Invalid relayer address");
        gasRelayer = _relayer;
    }

    function setGasAdvanceFee(uint256 _fee) external onlyOwner {
        gasAdvanceFee = _fee;
    }

    function canActivateArena(address _user) external view returns (bool) {
        return userPoints[_user] >= ARENA_ACTIVATION_POINTS;
    }

    function activateArenaWithPoints(uint256 _duelId) external {
        require(_duelId < nextDuelId, "Duel does not exist");
        require(duels[_duelId].creator == msg.sender, "Not duel creator");
        require(userPoints[msg.sender] >= ARENA_ACTIVATION_POINTS, "Insufficient points");
        require(!duelArenaEligible[_duelId], "Already arena eligible");

        userPoints[msg.sender] -= ARENA_ACTIVATION_POINTS;
        duelArenaEligible[_duelId] = true;

        emit ArenaEligibilitySet(_duelId, true);
        emit PointsDeducted(msg.sender, ARENA_ACTIVATION_POINTS, "Arena activation");
    }

    function getUserPoints(address _user) external view returns (uint256) {
        return userPoints[_user];
    }

    function getGasFeeDebt(address _user) external view returns (uint256) {
        return gasFeeDebt[_user];
    }

    function manualGasRepayment(address _user, uint256 _amount) external onlyOwner {
        require(gasFeeDebt[_user] >= _amount, "Amount exceeds debt");
        gasFeeDebt[_user] -= _amount;
        require(bettingToken.transferFrom(_user, address(this), _amount), "Repayment failed");
        emit GasDebtRepaid(_user, _amount);
    }
}

